package ua.sourceit.ishop.core.service.impl;

import org.junit.Test;
import org.springframework.test.util.ReflectionTestUtils;
import ua.sourceit.ishop.core.dao.impl.hibernate.ImageDaoImpl;
import ua.sourceit.ishop.core.entity.Watch;
import ua.sourceit.ishop.core.entity.WatchImage;
import ua.sourceit.ishop.core.model.DtoConverter;
import ua.sourceit.ishop.core.model.ImageDto;
import ua.sourceit.ishop.core.service.ImageService;

import java.io.File;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.fail;
import static org.mockito.Mockito.*;

/**
 * @author: areznik
 */

public class ImageServiceImplTest {

    public static final String BASE64_IMG = "data:image/jpeg;base64,";

    @Test
    public void testSaveImageFromBase64WhenLinkIsBase64() throws Exception {
        ImageServiceImpl imageService = new ImageServiceImpl();
        ReflectionTestUtils.setField(imageService, "imagePath", "/var/image/");

        String path = imageService.saveImageAndGetLink(BASE64_IMG);

        File file = new File("/var/image/"+path);
        if(!file.exists()){
            fail("file in "+path+" does not exists!");
        }else{
            file.delete();
        }
    }

    @Test
    public void testSaveImageFromBase64WhenLinkIsNotBase64() throws Exception {
        String imgLink = "http://imageStorage/1.jpg";
        ImageServiceImpl imageService = new ImageServiceImpl();

        String path = imageService.saveImageAndGetLink(imgLink);

        assertEquals(path,imgLink);
    }


    @Test
    public void testDeleteImage() throws Exception{
        ImageDaoImpl imageDaoMock = mock(ImageDaoImpl.class);
        ImageService imageService = new ImageServiceImpl();
        WatchImage watchImage = new WatchImage();
        watchImage.setId(1003);
        watchImage.setBigImage("bigImgLink");
        watchImage.setSmallImage("smallImgLink");
        ReflectionTestUtils.setField(imageService, "imageDao", imageDaoMock);

        imageService.deleteImage(watchImage);

        verify(imageDaoMock,times(1)).delete(watchImage);
    }

    @Test
    public void testGetWatchImages() throws Exception{
        ImageDaoImpl imageDaoMock = mock(ImageDaoImpl.class);
        ImageService imageService = new ImageServiceImpl();
        ReflectionTestUtils.setField(imageService, "imageDao", imageDaoMock);
        Watch watch = new Watch();
        watch.setId(2312);

        imageService.getWatchImages(watch);

        verify(imageDaoMock,times(1)).getWatchImages(watch);
    }

    @Test
    public void testSaveImageWithHttpLinks() throws Exception{
        ImageDaoImpl imageDaoMock = mock(ImageDaoImpl.class);
        String imgLink = "http://imageStore/2.jpg";
        ImageDto imageDto = new ImageDto(102,imgLink);
        ImageService imageServiceSpy = spy(new ImageServiceImpl());
        ReflectionTestUtils.setField(imageServiceSpy, "imageDao", imageDaoMock);
        when(imageServiceSpy.saveImageAndGetLink(imgLink)).thenReturn(imgLink);

        imageServiceSpy.saveImage(imageDto);

        verify(imageServiceSpy,times(0)).saveImageAndGetLink(imgLink);
        verify(imageDaoMock,times(1)).save(DtoConverter.getWatchImage(imageDto));
    }

    @Test
    public void testSaveImageWithBase64Links() throws Exception{
        ImageDaoImpl imageDaoMock = mock(ImageDaoImpl.class);
        String imgLink = BASE64_IMG;
        ImageDto imageDto = new ImageDto(102,imgLink);
        ImageService imageServiceSpy = spy(new ImageServiceImpl());
        ReflectionTestUtils.setField(imageServiceSpy, "imageDao", imageDaoMock);
        doReturn(imgLink).when(imageServiceSpy).saveImageAndGetLink(imgLink);

        imageServiceSpy.saveImage(imageDto);

        verify(imageServiceSpy,times(2)).saveImageAndGetLink(imgLink);
        verify(imageDaoMock,times(1)).save(DtoConverter.getWatchImage(imageDto));
    }

}
